select count(*)
from tbl_calendar_small_category
where fk_lgcatgono = 1 and fk_employee_id = #{fk_employee_id} and smcatgoname = #{my_smcatgoname}

    	select smcatgono, fk_lgcatgono, smcatgoname
		from tbl_calendar_small_category
		where fk_lgcatgono = #{fk_lgcatgono} 
		
			and fk_employee_id= #{fk_employee_id}
		
		
		
			and fk_department_id= #{fk_department_id}
		
		order by smcatgono asc

mapper.update:
update tbl_calendar_schedule set startdate = to_date(#{startdate}, 'yyyymmddhh24miss')
                               , enddate = to_date(#{enddate}, 'yyyymmddhh24miss')
                               , subject = #{subject}
                               , fk_lgcatgono = #{fk_lgcatgono}
                               , fk_smcatgono = #{fk_smcatgono}
                               , color = #{color}
                               , place = #{place}
                               , joinuser = #{joinuser}
                               , content = #{content}
where scheduleno = #{scheduleno}

mapper.select:
    select count(*)
	from tbl_calendar_small_category
	where fk_lgcatgono = #{caltype} 
	and fk_employee_id = #{employee_id} 
	and smcatgoname = #{smcatgoname}

select scheduleno , startdate, enddate , smcatgoname, lgcatgoname, name , subject, content from ( select row_number() over(order by SD.scheduleno desc) as rno , SD.scheduleno , to_char(SD.startdate, 'yyyy-mm-dd hh24:mi') as startdate , to_char(SD.enddate, 'yyyy-mm-dd hh24:mi') as enddate , SC.smcatgoname, LC.lgcatgoname, M.name , SD.subject, SD.content from tbl_calendar_schedule SD JOIN TBL_EMPLOYEES M ON SD.fk_employee_id = M.employee_id JOIN tbl_calendar_small_category SC ON SD.fk_smcatgono = SC.smcatgono JOIN tbl_calendar_large_category LC ON SD.fk_lgcatgono = LC.lgcatgono
where ( to_char(SD.startdate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) AND ( to_char(SD.enddate,'YYYY-MM-DD') between #{startdate} and #{enddate} )
and ( SD.fk_lgcatgono = 2 or SD.fk_employee_id = #{fk_employee_id} OR ( SD.fk_lgcatgono = 3 and SD.fk_department_id = #{fk_department_id}) OR ( SD.fk_employee_id != #{fk_employee_id} and lower(SD.joinuser) like '%'||lower(#{fk_email})||'%' ) )
and ( SD.fk_lgcatgono = 2 )
and ( SD.fk_lgcatgono = 1 AND SD.fk_employee_id = #{fk_employee_id} )
and ( SD.fk_lgcatgono = 3 AND SD.fk_department_id = #{fk_department_id} )
and SD.fk_lgcatgono = 1 and SD.fk_employee_id = #{fk_employee_id} and lower(SD.joinuser) like '%'||lower(#{searchWord})||'%'
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
and ( SD.fk_lgcatgono = 2 or SD.fk_employee_id = #{fk_employee_id} OR ( SD.fk_lgcatgono = 3 and SD.fk_department_id = #{fk_department_id}) OR ( SD.fk_employee_id != #{fk_employee_id} and lower(SD.joinuser) like '%'||lower(#{fk_email})||'%' ) )
and SD.fk_lgcatgono = 1 and SD.fk_employee_id = #{fk_employee_id}
and SD.fk_lgcatgono = 3 and SD.fk_department_id = #{fk_department_id}
and SD.fk_lgcatgono = 2
) V where V.rno between #{startRno} and #{endRno}

select count(*)
from tbl_calendar_small_category
where fk_lgcatgono = 3 and smcatgoname = #{dep_smcatgoname}

select count(*) from ( select row_number() over(order by reservationno desc) as rno, reservationno, to_char(startdate, 'yyyymmddhh24') as startdate, to_char(enddate, 'yyyymmddhh24') as enddate, realuser, fk_smcatgono, fk_lgcatgono, to_char(reservdate, 'yyyymmddhh24') as reservdate, confirm, R.status, return_time, name, fk_employee_id, fk_department_id from tbl_reservation R join tbl_employees E ON R.fk_employee_id = E.employee_id where R.reservationno < 9000
and (( to_char(startdate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) or ( to_char(enddate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) )
and R.fk_lgcatgono = #{pagination.searchType}
and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
and R.fk_lgcatgono = #{pagination.searchType} and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
)

select reservationno, startdate, enddate, realuser, fk_smcatgono, fk_lgcatgono, reservdate, confirm, status, return_time, name, fk_employee_id, fk_department_id, smcatgoname, startdate_view, enddate_view from ( select row_number() over(order by reservationno desc) as rno, reservationno, to_char(startdate, 'yyyymmddhh24') as startdate, to_char(enddate, 'yyyymmddhh24') as enddate, to_char(startdate, 'yyyy-mm-dd hh24:mi') as startdate_view, to_char(enddate, 'yyyy-mm-dd hh24:mi') as enddate_view, realuser, R.fk_smcatgono, R.fk_lgcatgono, smcatgoname, to_char(reservdate, 'yyyymmddhh24') as reservdate, confirm, R.status, return_time, name, R.fk_employee_id, fk_department_id from tbl_reservation R join tbl_employees E ON R.fk_employee_id = E.employee_id join tbl_reservation_small_category C ON R.fk_smcatgono = C.smcatgono where R.reservationno < 9000
and (( to_char(startdate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) or ( to_char(enddate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) )
and R.fk_lgcatgono = #{pagination.searchType}
and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
and R.fk_lgcatgono = #{pagination.searchType} and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
) where rno between #{pagination.startRno} and #{pagination.endRno}

select count(*) from ( select row_number() over(order by reservationno desc) as rno, R.reservationno, to_char(startdate, 'yyyymmddhh24') as startdate, to_char(enddate, 'yyyymmddhh24') as enddate, R.realuser, R.fk_smcatgono, R.fk_lgcatgono, to_char(reservdate, 'yyyymmddhh24') as reservdate, R.confirm, R.status, R.return_time, E.name, R.fk_employee_id, E.fk_department_id from tbl_reservation R join tbl_employees E ON R.fk_employee_id = E.employee_id where (R.fk_employee_id = #{employee_id} or lower(R.realuser) like '%'||lower(#{email})||'%') and R.reservationno < 9000
and ((to_char(startdate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) or ( to_char(enddate,'YYYY-MM-DD') between #{startdate} and #{enddate} ))
and R.fk_lgcatgono = #{pagination.searchType}
and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
and R.fk_lgcatgono = #{pagination.searchType} and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
)

select reservationno, startdate, enddate, realuser, fk_smcatgono, fk_lgcatgono, reservdate, confirm, status, return_time, name, fk_employee_id, fk_department_id, smcatgoname, startdate_view, enddate_view from ( select row_number() over(order by reservationno desc) as rno, reservationno, to_char(startdate, 'yyyymmddhh24') as startdate, to_char(enddate, 'yyyymmddhh24') as enddate, to_char(startdate, 'yyyy-mm-dd hh24:mi') as startdate_view, to_char(enddate, 'yyyy-mm-dd hh24:mi') as enddate_view, realuser, R.fk_smcatgono, R.fk_lgcatgono, smcatgoname, to_char(reservdate, 'yyyymmddhh24') as reservdate, confirm, R.status, return_time, E.name, R.fk_employee_id, fk_department_id from tbl_reservation R join tbl_employees E ON R.fk_employee_id = E.employee_id join tbl_reservation_small_category C ON R.fk_smcatgono = C.smcatgono where (R.fk_employee_id = #{employee_id} or lower(R.realuser) like '%'||lower(#{email})||'%') and R.reservationno < 9000
and ((to_char(startdate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) or ( to_char(enddate,'YYYY-MM-DD') between #{startdate} and #{enddate} ))
and R.fk_lgcatgono = #{pagination.searchType}
and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
and R.fk_lgcatgono = #{pagination.searchType} and (lower(realuser) like '%'||lower(#{pagination.searchWord})||'%' or lower(name) like '%'||lower(#{pagination.searchWord})||'%')
) where rno between #{pagination.startRno} and #{pagination.endRno}

select smcatgono, smcatgoname, sc_status, C.fk_employee_id, lgcatgono, lgcatgoname from tbl_reservation_small_category C JOIN tbl_reservation_large_category L ON C.fk_lgcatgono = L.lgcatgono
where L.lgcatgono = #{fk_lgcatgono}
order by lgcatgono, smcatgono

