mapper.select:
SELECT COUNT(*) FROM ( SELECT row_number() over(order by seq desc) as rno FROM tbl_document WHERE 1=1
AND (LOWER(documentsubject) LIKE '%' || LOWER(#{pagination.searchWord}) || '%')
)

select seq, documentsubject from ( select row_number() over(order by seq desc) as rno, seq, documentsubject from tbl_document where 1=1
and (lower(documentsubject) like '%'||lower(#{pagination.searchWord})||'%')
) where rno between #{pagination.startRno} and #{pagination.endRno}
